mapper.insert:
INSERT INTO SysAuthModule

    SELECT NEWID(),#{item.toRoleId},#{item.toAccount},nameSpace,moduleName,amStatus,aoStatus,adStatus
    FROM SysAuthModule

        roleId=#{item.fromRoleId}
        AND account=#{item.fromAccount}
        AND nameSpace=#{item.nameSpace}
        AND moduleName=#{item.moduleName}

mapper.update:
UPDATE SysAuthModule
roleId=#{roleId}, account=#{account}, nameSpace=#{nameSpace}, moduleName=#{moduleName}, amStatus=#{amStatus}, aoStatus=#{aoStatus}, adStatus=#{adStatus},
WHERE amId=#{amId}

UPDATE SysAuthModule SET amId=a.amId,roleId=a.roleId,account=a.account,nameSpace=a.nameSpace,moduleName=a.moduleName,amStatus=a.amStatus,aoStatus=a.aoStatus,adStatus=a.adStatus FROM (
SELECT #{item.amId} as amId,#{item.roleId} as roleId,#{item.account} as account,#{item.nameSpace} as nameSpace,#{item.moduleName} as moduleName,#{item.amStatus} as amStatus,#{item.aoStatus} as aoStatus,#{item.adStatus} as adStatus
) AS a WHERE SysAuthModule.amId=a.amId

mapper.delete:
DELETE SysAuthModule


        (roleId=#{item.toRoleId} AND account=#{item.toAccount}
        AND nameSpace=#{item.nameSpace}
        AND moduleName=#{item.moduleName}
        )

DELETE SysAuthModule


        (roleId=#{item.roleId} AND account=#{item.account}
        AND nameSpace=#{item.nameSpace}
        AND moduleName=#{item.moduleName}
        )

mapper.select:
SELECT amId,roleId,account,nameSpace,moduleName,amStatus,aoStatus,adStatus
FROM SysAuthModule

    amId=#{amId}
    AND roleId=#{roleId}
    AND account=#{account}
    AND nameSpace=#{nameSpace}
    AND moduleName=#{moduleName}
