mapper.update:
UPDATE SysDataDictionary

    dbInstance=#{dbInstance},
    dbName=#{dbName},
    tableName=#{tableName},
    fieldName=#{fieldName},
    alias=#{alias},
    type=#{type},
    length=#{length}


    dbInstance=#{dbInstance}
    AND dbName=#{dbName}
    AND tableName=#{tableName}
    AND fieldName=#{fieldName}
    AND alias=#{alias}
    AND type=#{type}

UPDATE SysDataDictionary SET dbInstance=a.dbInstance,dbName=a.dbName,tableName=a.tableName,fieldName=a.fieldName,alias=a.alias,type=a.type,length=a.length FROM (
SELECT #{item.dbInstance} as dbInstance,#{item.dbName} as dbName,#{item.tableName} as tableName,#{item.fieldName} as fieldName,#{item.alias} as alias,#{item.type} as type,#{item.length} length operating
) AS a WHERE SysDataDictionary.dbInstance=a.dbInstance AND SysDataDictionary.dbName=a.dbName AND SysDataDictionary.tableName=a.tableName ANDSysDataDictionary.fieldName=a.fieldName

mapper.select:
SELECT dbInstance,dbName,tableName,fieldName,alias,type,length
FROM SysDataDictionary

    dbInstance=#{dbInstance}
    AND dbName=#{dbName}
    AND tableName=#{tableName}
    AND fieldName=#{fieldName}
    AND alias=#{alias}
    AND type=#{type}
