mapper.select:
SELECT a.id,a.projNo,a.projName,a.month,a.deptName,a.team,a.attachment,a.comment,a.status,a.creatorAcc,a.creatorName,a.createTime,b.id AS idDetl,b.account AS accountDetl,b.content AS contentDetl,b.matQty AS matQtyDetl,b.spec AS specDetl,b.unit AS unitDetl,b.price AS priceDetl,b.amount AS amountDetl,b.attachment AS attachmentDetl,b.comment AS commentDetl,b.creatorAcc AS creatorAccDetl,b.creatorName AS creatorNameDetl,b.createTime AS createTimeDetl
FROM SmSbSettlementDetail b
LEFT JOIN SmSbSettlement a ON a.id=b.pid

    a.id=#{id}
    AND a.projNo=#{projNo}
    AND a.projName=#{projName}
    AND DATEDIFF(MONTH,#{month},a.month)=0
    AND a.deptName=#{deptName}
    AND a.status=#{status}
    AND a.team LIKE '%' + #{team} + '%'
    AND a.comment LIKE '%' + #{comment} + '%'
    AND a.creatorAcc=#{creatorAcc}
    AND b.idDetl=#{idDetl}
    AND b.account=#{accountDetl}
    AND b.spec=#{specDetl}
    AND b.unit=#{unitDetl}
    AND b.comment LIKE '%' + #{commentDetl} + '%'
    AND b.creatorAcc=#{creatorAccDetl}

ORDER BY a.month DESC,a.projNo,a.deptName,a.team,b.account,b.createTime
