mapper.update:
UPDATE SmSbTargetCost
projNo=#{projNo}, projName=#{projName}, deptName=#{deptName}, amount=#{amount},
WHERE id=#{id}

mapper.select:
SELECT NEWID() AS id, a.projNo,a.projName,a.deptName,a.amount,(CASE WHEN b.distribution IS NULl THEN 0 ELSE b.distribution END) AS distribution
FROM (SELECT t.projNo,t.ProjName,t.deptName,SUM(t.amount) AS amount
       FROM (SELECT projNo,projName,deptName,amount
                FROM SmSbTargetCost

                    id=#{id}
                    AND projNo=#{projNo}
                    AND projName=#{projName}
                    AND deptName=#{deptName}

                UNION ALL
                SELECT projNo,projName,deptName,amount
                FROM [code].[dbo].[V_TargetCost]

                    id=#{id}
                    AND projNo=#{projNo}
                    AND projName=#{projName}
                    AND deptName=#{deptName}
                ) t
       GROUP BY t.projNo,t.projName,t.deptName
) a
LEFT JOIN (SELECT projNo,deptName,SUM(amount) AS distribution
            FROM SmSbTargetCostSplit

                AND projNo=#{projNo}
                AND deptName=#{deptName}

            GROUP BY projNo,deptName) b
ON a.projNo=b.projNo AND a.deptName=b.deptName
ORDER BY a.projName,a.deptName
