mapper.update:
UPDATE SmSrSettlementDetlFirst
pid=#{pid}, specs=#{specs}, type=#{type}, applyContent=#{applyContent}, adjustContent=#{adjustContent}, applyQty1=#{applyQty1}, applyQty2=#{applyQty2}, applyQty3=#{applyQty3}, applyQty4=#{applyQty4}, applyQty5=#{applyQty5}, applyQty6=#{applyQty6}, adjustQty1=#{adjustQty1}, adjustQty2=#{adjustQty2}, adjustQty3=#{adjustQty3}, adjustQty4=#{adjustQty4}, adjustQty5=#{adjustQty5}, adjustQty6=#{adjustQty6}, settleQty1=#{settleQty1}, settleQty2=#{settleQty2}, settleQty3=#{settleQty3}, settleQty4=#{settleQty4}, settleQty5=#{settleQty5}, settleQty6=#{settleQty6}, price=#{price}, amount=#{amount}, attachment=#{attachment}, comment=#{comment}, confirmAcc=#{confirmAcc}, confirmName=#{confirmName}, confirmTime=#{confirmTime},
WHERE id=#{id}

UPDATE SmSrSettlementDetlFirst SET id=a.id,pid=a.pid,specs=a.specs,type=a.type,applyContent=a.applyContent,adjustContent=a.adjustContent,applyQty1=a.applyQty1,applyQty2=a.applyQty2,applyQty3=a.applyQty3,applyQty4=a.applyQty4,applyQty5=a.applyQty5,applyQty6=a.applyQty6,adjustQty1=a.adjustQty1,adjustQty2=a.adjustQty2,adjustQty3=a.adjustQty3,adjustQty4=a.adjustQty4,adjustQty5=a.adjustQty5,adjustQty6=a.adjustQty6,settleQty1=a.settleQty1,settleQty2=a.settleQty2,settleQty3=a.settleQty3,settleQty4=a.settleQty4,settleQty5=a.settleQty5,settleQty6=a.settleQty6,price=a.price,amount=a.amount,attachment=a.attachment,comment=a.comment,confirmAcc=a.confirmAcc,confirmName=a.confirmName,confirmTime=a.confirmTime FROM (
SELECT #{item.id} as id,#{item.pid} as pid,#{item.specs} as specs,#{item.type} as type,#{item.applyContent} as applyContent,#{item.adjustContent} as adjustContent,#{item.applyQty1} as applyQty1,#{item.applyQty2} as applyQty2,#{item.applyQty3} as applyQty3,#{item.applyQty4} as applyQty4,#{item.applyQty5} as applyQty5,#{item.applyQty6} as applyQty6,#{item.adjustQty1} as adjustQty1,#{item.adjustQty2} as adjustQty2,#{item.adjustQty3} as adjustQty3,#{item.adjustQty4} as adjustQty4,#{item.adjustQty5} as adjustQty5,#{item.adjustQty6} as adjustQty6,#{item.settleQty1} as settleQty1,#{item.settleQty2} as settleQty2,#{item.settleQty3} as settleQty3,#{item.settleQty4} as settleQty4,#{item.settleQty5} as settleQty5,#{item.settleQty6} as settleQty6,#{item.price} as price,#{item.amount} as amount,#{item.attachment} as attachment,#{item.comment} as comment,#{item.confirmAcc} as confirmAcc,#{item.confirmName} as confirmName,#{item.confirmTime} as confirmTime
) AS a WHERE SmSrSettlementDetlFirst.id=a.id

mapper.select:
SELECT id,pid,specs,type,applyContent,adjustContent,applyQty1,applyQty2,applyQty3,applyQty4,applyQty5,applyQty6,adjustQty1,adjustQty2,adjustQty3,adjustQty4,adjustQty5,adjustQty6,settleQty1,settleQty2,settleQty3,settleQty4,settleQty5,settleQty6,price,amount,attachment,comment,creatorAcc,creatorName,createTime,confirmAcc,confirmName,confirmTime
FROM SmSrSettlementDetlFirst

    id=#{id}
    AND pid=#{pid}
    AND specs=#{specs}
    AND type=#{type}
    AND applyContent LIKE '%' + #{applyContent} + '%'
    AND adjustContent LIKE '%' + #{adjustContent} + '%'
    AND comment LIKE '%' + #{comment} + '%'
    AND creatorAcc=#{creatorAcc}
    AND confirmAcc=#{confirmAcc}

ORDER BY pid,createTime,id
