mapper.update:
UPDATE XrSrSettlement
id=#{id}, progress=#{progress}, projNo=#{projNo}, projName=#{projName}, dept=#{dept}, team=#{team}, isOutwork=#{isOutwork}, amountFirst=#{amountFirst}, amountSecond=#{amountSecond}, attachment=#{attachment}, balApplAtt=#{balApplAtt}, comment=#{comment}, status=#{status}, isQuota=#{isQuota}, creatorAcc=#{creatorAcc}, creatorName=#{creatorName}, createTime=#{createTime}, teamAcc=#{teamAcc}, teamName=#{teamName}, score=#{score}, opinion=#{opinion}, settFirstAcc=#{settFirstAcc}, settFirstName=#{settFirstName}, settFirstTime=#{settFirstTime}, settlementTime=#{settlementTime},
WHERE id=#{id}

UPDATE XrSrSettlement SET id=a.id,progress=a.progress,projNo=a.projNo,projName=a.projName,dept=a.dept,team=a.team,isOutwork=a.isOutwork,amountFirst=a.amountFirst,amountSecond=a.amountSecond,attachment=a.attachment,balApplAtt=a.balApplAtt,comment=a.comment,status=a.status,isQuota=a.amountSecond,isQuota=a.creatorAcc,creatorName=a.creatorName,createTime=a.createTime,teamAcc=a.teamAcc,teamName=a.teamName,score=a.score,opinion=a.opinion,settFirstAcc=a.settFirstAcc,settFirstName=a.settFirstName,settFirstTime=a.settFirstTime,settlementTime=a.settlementTime FROM (
SELECT #{item.id} as id,#{item.progress} as progress,#{item.projNo} as projNo,#{item.projName} as projName,#{item.dept} as dept,#{item.team} as team,#{item.isOutwork} as isOutwork,#{item.amountFirst} as amountFirst,#{item.amountSecond} as amountSecond,#{item.attachment} as attachment,#{item.balApplAtt} as balApplAtt,#{item.comment} as comment,#{item.status} as status,#{item.isQuota} as isQuota,#{item.creatorAcc} as creatorAcc,#{item.creatorName} as creatorName,#{item.createTime} as createTime,#{item.teamAcc} as teamAcc,#{item.teamName} as teamName,#{item.score} as score,#{item.opinion} as opinion,#{item.settFirstAcc} as settFirstAcc,#{item.settFirstName} as settFirstName,#{item.settFirstTime} as settFirstTime,#{item.settlementTime} as settlementTime
) AS a WHERE XrSrSettlement.id=a.id

mapper.select:
SELECT id,progress,projNo,projName,dept,team,isOutwork,amountFirst,amountSecond,attachment,balApplAtt,comment,status,isQuota,creatorAcc,creatorName,createTime,teamAcc,teamName,score,opinion,settFirstAcc,settFirstName,settFirstTime,settlementTime
FROM XrSrSettlement

    id=#{id}
    AND projNo=#{projNo}
    AND projName=#{projName}
    AND dept=#{dept}
    AND team=#{team}
    AND attachment=#{attachment}
    AND comment=#{comment}
    AND status=#{status}
    AND creatorAcc=#{creatorAcc}
    AND creatorName=#{creatorName}
    AND teamAcc=#{teamAcc}
    AND teamName=#{teamName}
    AND opinion=#{opinion}
    AND settFirstAcc=#{settFirstAcc}
    AND settFirstName=#{settFirstName}

ORDER BY id
